import {ethers, Wallet} from "ethers";
import Comptroller from "./Comptroller";

export default class Tropykus {
    constructor(providerURL) {
        this.ethersProvider = new ethers.providers.JsonRpcProvider(providerURL);
        this.internalComptroller = null;
        this.internalAccount = null;
    }

    /**
     * Returns the initialized comptroller instance.
     * @return {Comptroller}
     */
    get comptroller() { return this.internalComptroller; }

    /**
     * By providing the on chain deployed comptroller address, a comptroller instance is made available.
     * @param {string} comptrollerAddress on chain deployed comptroller address.
     */
    setComptroller(comptrollerAddress) {
        this.internalComptroller = new Comptroller(comptrollerAddress, this.ethersProvider);
    }

    /**
     * Returns the initialized account instance.
     * @return {Object}
     */
    get account() { return this.internalAccount; }

    /**
     * By providing the mnemonic, a wallet instance is made available.
     * @param {string} mnemonic to generate the wallet.
     */
    setAccount(mnemonic) {
        this.internalAccount = Wallet.fromMnemonic(mnemonic, `m/44'/60'/0'/0/0`).connect(this.ethersProvider);
    }
}

import {ethers} from "ethers";
import ComptrollerAbi from '../abis/ComptrollerG6.json';

export default class Comptroller {
    constructor(contractAddress, ethersProvider) {
        this.ethersProvider = ethersProvider;
        this.instance = new ethers.Contract(contractAddress, ComptrollerAbi, this.ethersProvider);
    }

    allMarkets() {
        return new Promise((resolve, reject) => {
            this.instance.callStatic.getAllMarkets()
                .then(resolve)
                .catch(reject);
        });
    }

    getAssetsIn(account) {
        return new Promise((resolve, reject) => {
            this.instance.callStatic.getAssetsIn(account.address)
            .then(resolve)
            .catch(reject);
        });
    }

    enterMarkets(account, marketAddresses) {
        return new Promise((resolve,reject) => { 
            this.instance.connect(account).enterMarkets(marketAddresses)
                .then(resolve)
                .catch(reject);
        });
    }
}

import CRBTCMarket from "./Markets/CRBTCMarket";
import CTokenMarket from "./Markets/CTokenMarket";

export default class Market {
    constructor(ethersProvider) {
        this.ethersProvider = ethersProvider;
        this.internalMarket = null;
    }

    get market() { return this.internalMarket; }

    setMarket(contractAddress, isCRBTCMarket = true) {
        if (isCRBTCMarket) {
            this.internalMarket = new CRBTCMarket(contractAddress, this.ethersProvider);
        } else {
            this.internalMarket = new CTokenMarket(contractAddress, this.ethersProvider);
        }
    }

    balanceOfUnderlying(account) {
        return this.market.balanceOfUnderlying(account);
    }

    supply(account, amount) {
        return this.internalMarket.supply(account, amount);
    }
}

import { ethers } from "ethers";
import BaseMarket from "./BaseMarket";
import CRBTCAbi from '../../abis/CRBTC.json';

export default class CRBTCMarket extends BaseMarket {
    constructor(contractAddress, ethersProvider) {
        super(ethersProvider, new ethers.Contract(contractAddress, CRBTCAbi, ethersProvider));
    }
}

export default class BaseMarket {
    constructor(ethersProvider, instance) {
        this.ethersProvider = ethersProvider;
        this.instance = instance;
    }

    balanceOfUnderlying(account) {
        return new Promise((resolve, reject) => {
            this.instance.callStatic.balanceOf(account.address)
            .then(resolve)
            .catch(reject);
        });
    }

    supply(account, amount) {
        return new Promise((resolve, reject) => {
            this.instance.connect(account).mint({ value: amount, gasLimit: this.gasLimit })
            .then(resolve)
            .catch(reject);
        });
    }
}

import { ethers } from "ethers";
import BaseMarket from "./BaseMarket";
import StandardTokenAbi from '../../abis/StandardToken.json';

export default class CRBTCMarket extends BaseMarket {
    constructor(contractAddress, ethersProvider) {
        super(ethersProvider, new ethers.Contract(contractAddress, StandardTokenAbi, ethersProvider));
    }
}
